import React, { useState, useEffect } from "react";
import Joke from "./Joke";
//HOOKS
import GetJokes from "./Hooks/GetJokes";
//STYLES
import "./Styles/JokeList.css";

const JokeListHooks = ({ toGet = 5 }) => {
  const [jokes, setJokes] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const allJokes = async () => {
      let { listJokes, checkLoad } = await GetJokes(toGet);
      console.log(listJokes);
      // console.log(checkLoad);
      setJokes((jokes) => {
        return listJokes;
      });
      setIsLoading((isLoading) => {
        return checkLoad;
      });
    };

    if (jokes.length === 0) allJokes();
  }, [jokes]);

  const vote = (id, num) => {
    console.log(id);
    setJokes((allJokes) =>
      allJokes.map((j) => (j.id === id ? { ...j, votes: j.votes + num } : j))
    );
  };
  return (
    <div className="JokeList">
      {/* <button className="JokeList-getmore" onClick={this.generateNewJokes}>
        Get New Jokes
      </button> */}

      {jokes.map((j) => (
        <Joke
          text={j.joke.joke}
          key={j.joke.id}
          id={j.joke.id}
          votes={j.votes}
          vote={vote}
        />
      ))}
    </div>
  );
};
export default JokeListHooks;
